={{   $('Define Timeout1').item.json.message }} 
{{
  $node['Webhook'].json.body.data.message.imageMessage?.caption
    ? (
        $json.content 
          ? `${$json.content} Legenda: ${$node['Webhook'].json.body.data.message.imageMessage.caption } }` 
          : 'Erro: json.content ausente para imagem com legenda'
      ) 
    : `${$json.content || ''} ${$json.text ? ' Transcrição: ' + $json.text : ''}`
}}

={{ 
    $json.message
        .split(/(https:\/\/[^\s]+?\.(?:jpeg|jpg|png|webp))/gi)
        .map(item => item === 'jpg' ? '' : item)// Remove espaços em branco no início e fim de cada item
        .filter(item => item !== '\\n\\n') // Remove itens vazios, nulos ou que sejam '\\n\\n'
        .filter(item => item !== '  ')
        .filter(item => item !== '   ')
        .filter(item => item !== '    ')
}}
