={{ (() => {
  const datas = $json.data
  const convertido = datas.map(item => {
    const parseDate = str => new Date(str.split('.')[0] + 'Z'); // Remove microssegundos e força como UTC
    const formatDate = date => new Date(date.getTime() - 3 * 60 * 60 * 1000).toISOString();

    return {
      start: {
        dateTime: formatDate(parseDate(item.start.dateTime)),
        timeZone: "UTC-3"
      },
      end: {
        dateTime: formatDate(parseDate(item.end.dateTime)),
        timeZone: "UTC-3"
      }
    };
  });

  return convertido;
})() }}

={{ (()=>{
  const dataInicial = $now.format('yyyy-MM-dd');
  diasUteis = 1 ;

  const feriadosFixosNacionais = [
    '01-01', // Confraternização Universal
    '04-21', // Tiradentes
    '05-01', // Dia do Trabalho
    '09-07', // Independência do Brasil
    '10-12', // Nossa Senhora Aparecida
    '11-02', // Finados
    '11-15', // Proclamação da República
    '12-25', // Natal
  ];

  const feriadosFixosEstaduaisES = [
    '05-23', // Colonização do Solo Espírito-Santense
    '10-28', // Dia do Servidor Público (opcional, mas incluído aqui)
  ];


  function calcularPascoa(ano) {
    const f = Math.floor, G = ano % 19;
    const C = f(ano / 100);
    const H = (C - f(C / 4) - f((8 * C + 13) / 25) + 19 * G + 15) % 30;
    const I = H - f(H / 28) * (1 - f(29 / (H + 1)) * f((21 - G) / 11));
    const J = (ano + f(ano / 4) + I + 2 - C + f(C / 4)) % 7;
    const L = I - J;
    const mes = 3 + f((L + 40) / 44);
    const dia = L + 28 - 31 * f(mes / 4);
    return new Date(ano, mes - 1, dia);
  }

  function obterFeriadosMoveis(ano) {
    const pascoa = calcularPascoa(ano);
    return [
      pascoa,
      new Date(pascoa.getTime() - 2 * 24 * 60 * 60 * 1000), // Sexta-feira Santa
      new Date(pascoa.getTime() - 47 * 24 * 60 * 60 * 1000), // Carnaval
      new Date(pascoa.getTime() + 60 * 24 * 60 * 60 * 1000), // Corpus Christi
    ].map(d => d.toISOString().slice(0, 10));
  }

  const data = new Date(dataInicial);
  const ano = data.getFullYear();
  const feriadosMoveis = obterFeriadosMoveis(ano);

  let adicionados = 0;
  while (adicionados < diasUteis) {
    data.setDate(data.getDate() + 1);
    const diaSemana = data.getDay();
    const dataISO = data.toISOString().slice(0, 10);
    const mmdd = dataISO.slice(5);

    const ehFeriado = feriadosFixosNacionais.includes(mmdd) ||
                      feriadosFixosEstaduaisES.includes(mmdd) ||
                      feriadosMoveis.includes(dataISO);

    const ehFimDeSemana = diaSemana === 0 || diaSemana === 6;

    if (!ehFeriado && !ehFimDeSemana) {
      adicionados++;
    }
  }


  return data.toISOString().slice(0, 10);

})()
}}

