={{ (()=>{

  var entrada = $('Webhook').item.json.body.data.key.remoteJid

    const partes = entrada.split('@');

    // Pegar a primeira parte
    let primeiraParte = partes[0];

    // Verificar se tem 13 caracteres
    if (primeiraParte.length !== 13) {
        // Inserir '9' na 5ª posição (índice 4)
        primeiraParte = primeiraParte.slice(0, 4) + '9' + primeiraParte.slice(4);
    }

return primeiraParte

})()
}}
